import { useEffect, useState, useRef } from 'react';

/* ===== MAP PARAMS ===== */
const GRID_SIZE = 20;
const MAP_SIZE = 800;
const GRID_COUNT = MAP_SIZE / GRID_SIZE;

function App() {
    const [mode, setMode] = useState('place'); // place | object
    const [gridMap, setGridMap] = useState({});
    const [selectedCells, setSelectedCells] = useState([]);
    const [isDragging, setIsDragging] = useState(false);
    const draggedRef = useRef(false);
    const [coordMode, setCoordMode] = useState(false); // Ï¢åÌëúÏßÄÏ†ï Î™®Îìú
    const [coordStep, setCoordStep] = useState('IDLE'); // Ï¢åÌëúÏßÄÏ†ï Îã®Í≥Ñ
    const [gridPoints, setGridPoints] = useState([]);   // gx, gy
    const [worldPoints, setWorldPoints] = useState([]); // x, y


    /* ===== JSON LOAD ===== */
    useEffect(() => {
        fetch('/semantic_grid_map.json')
            .then((res) => (res.ok ? res.json() : null))
            .then((data) => {
                if (data?.grid) setGridMap(data.grid);
            })
            .catch(() => console.log('no json'));
    }, []);
    useEffect(() => {
    if (coordStep === 'COLLECT' && gridPoints.length === 5) {
        setCoordStep('INPUT');
    }
}, [gridPoints, coordStep]);
useEffect(() => {
    if (coordStep !== 'INPUT') return;

    const inputWorldCoords = async () => {
        const labels = [
            'Ï§ëÏã¨ Ï¢åÌëú (0,0)',
            'ÏôºÏ™Ω ÏúÑ',
            'Ïò§Î•∏Ï™Ω ÏúÑ',
            'ÏôºÏ™Ω ÏïÑÎûò',
            'Ïò§Î•∏Ï™Ω ÏïÑÎûò',
        ];

        const result = [];

        for (let i = 0; i < 5; i++) {
            const x = Number(prompt(`${labels[i]} - world X Ï¢åÌëú`));
            const y = Number(prompt(`${labels[i]} - world Y Ï¢åÌëú`));

            if (isNaN(x) || isNaN(y)) {
                alert('Ïà´ÏûêÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî');
                i--;
                continue;
            }

            result.push({ x, y });
        }

        setWorldPoints(result);
        setCoordStep('IDLE');
        setCoordMode(false);

        alert('Ï¢åÌëú ÏûÖÎ†• ÏôÑÎ£å');
    };

    inputWorldCoords();
}, [coordStep]);



    /* ===== GRID SORT (y desc ‚Üí x desc) ===== */
    const sortGridDesc = (grid) => {
        return Object.fromEntries(
            Object.entries(grid).sort(([a], [b]) => {
                const [ax, ay] = a.split(',').map(Number);
                const [bx, by] = b.split(',').map(Number);
                if (ay !== by) return by - ay;
                return bx - ax;
            })
        );
    };

    /* ===== JSON SAVE ===== */
    const saveJSON = () => {
        const sortedGrid = sortGridDesc(gridMap);

        const blob = new Blob([JSON.stringify({ grid: sortedGrid }, null, 2)], {
            type: 'application/json',
        });

        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'semantic_grid_map.json';
        a.click();
        URL.revokeObjectURL(url);
    };



    /* ===== GRID CLICK (ÏÑ†ÌÉùÎßå Îã¥Îãπ) ===== */
    const handleGridClick = (gx, gy) => {
        const exists = selectedCells.some((c) => c.x === gx && c.y === gy);

        if (exists) {
            setSelectedCells((prev) =>
                prev.filter((c) => !(c.x === gx && c.y === gy))
            );
        } else {
            setSelectedCells((prev) => [...prev, { x: gx, y: gy }]);
        }
    };

    const addSelectedCell = (gx, gy) => {
        setSelectedCells((prev) => {
            if (prev.some((c) => c.x === gx && c.y === gy)) return prev;
            return [...prev, { x: gx, y: gy }];
        });
    };

    /* ===== APPLY PLACE ===== */
    const applyPlaceToSelected = () => {
        if (selectedCells.length === 0) return;

        const name = prompt('place name?');
        const relation = prompt('relation?');
        if (!name) return;

        setGridMap((prev) => {
            const updated = { ...prev };

            selectedCells.forEach(({ x, y }) => {
                const key = `${x},${y}`;
                const cell = updated[key] || {
                    places: [],
                    objects: [],
                    robots: [],
                };

