                {/* GRID LINES */}
                {[...Array(GRID_COUNT)].map((_, i) => (
                    <div
                        key={`v${i}`}
                        style={{
                            position: 'absolute',
                            left: i * GRID_SIZE,
                            top: 0,
                            width: 1,
                            height: '100%',
                            background: 'rgba(0,0,0,0.2)',
                        }}
                    />
                ))}
                {[...Array(GRID_COUNT)].map((_, i) => (
                    <div
