import { useEffect, useState, useRef } from 'react';

/* ===== MAP PARAMS ===== */
const GRID_SIZE = 20;
const MAP_SIZE = 800;
const GRID_COUNT = MAP_SIZE / GRID_SIZE;

function App() {
    const [mode, setMode] = useState('place'); // place | object
    const [gridMap, setGridMap] = useState({});
    const [selectedCells, setSelectedCells] = useState([]);
    const [isDragging, setIsDragging] = useState(false);
    const draggedRef = useRef(false);
    const [coordPoints, setCoordPoints] = useState([]); 

    /* ===== JSON LOAD ===== */
    useEffect(() => {
        fetch('/semantic_grid_map.json')
            .then((res) => (res.ok ? res.json() : null))
            .then((data) => {
                if (data?.grid) setGridMap(data.grid);
            })
            .catch(() => console.log('no json'));
    }, []);

    /* ===== GRID SORT (y desc ‚Üí x desc) ===== */
    const sortGridDesc = (grid) => {
        return Object.fromEntries(
            Object.entries(grid).sort(([a], [b]) => {
                const [ax, ay] = a.split(',').map(Number);
                const [bx, by] = b.split(',').map(Number);
                if (ay !== by) return by - ay;
                return bx - ax;
            })
        );
    };

    /* ===== JSON SAVE ===== */
    const saveJSON = () => {
        const sortedGrid = sortGridDesc(gridMap);

        const blob = new Blob([JSON.stringify({ grid: sortedGrid }, null, 2)], {
            type: 'application/json',
        });

        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'semantic_grid_map.json';
        a.click();
        URL.revokeObjectURL(url);
    };

    const applyGridCoordinates = () => {
    if (coordPoints.length !== 5) {
        alert('Ï¢åÌëúÎäî Ï†ïÌôïÌûà 5Í∞ú Ï∞çÏñ¥Ïïº Ìï©ÎãàÎã§');
        return;
    }

    const [origin, lt, rt, lb, rb] = coordPoints;

    // grid Í∏∞Ï§Ä Ìè≠/ÎÜíÏù¥
    const gridWidth = rt.gx - lt.gx;
    const gridHeight = lb.gy - lt.gy;

    // Ïã§Ï†ú Ï¢åÌëú Í∏∞Ï§Ä Ìè≠/ÎÜíÏù¥
    const worldWidth = rt.x - lt.x;
    const worldHeight = lb.y - lt.y;

    const dx = worldWidth / gridWidth;
    const dy = worldHeight / gridHeight;

    setGridMap(prev => {
        const updated = { ...prev };

        Object.keys(updated).forEach(key => {
            const [gx, gy] = key.split(',').map(Number);

            const wx = lt.x + (gx - lt.gx) * dx;
            const wy = lt.y + (gy - lt.gy) * dy;

            updated[key] = {
                ...updated[key],
                explicit: {
                    ...(updated[key].explicit || {}),
                    world: {
                        x: Number(wx.toFixed(3)),
                        y: Number(wy.toFixed(3)),
                    },
                },
            };
        });

        return updated;
    });

    setCoordPoints([]);
};


    /* ===== GRID CLICK (ÏÑ†ÌÉùÎßå Îã¥Îãπ) ===== */
    const handleGridClick = (gx, gy) => {
        const exists = selectedCells.some((c) => c.x === gx && c.y === gy);

        if (exists) {
            setSelectedCells((prev) =>
                prev.filter((c) => !(c.x === gx && c.y === gy))
            );
        } else {
            setSelectedCells((prev) => [...prev, { x: gx, y: gy }]);
        }
    };

    const addSelectedCell = (gx, gy) => {
        setSelectedCells((prev) => {
            if (prev.some((c) => c.x === gx && c.y === gy)) return prev;
            return [...prev, { x: gx, y: gy }];
        });
    };

    /* ===== APPLY PLACE ===== */
    const applyPlaceToSelected = () => {
        if (selectedCells.length === 0) return;

        const name = prompt('place name?');
        const relation = prompt('relation?');
        if (!name) return;

        setGridMap((prev) => {
            const updated = { ...prev };

            selectedCells.forEach(({ x, y }) => {
                const key = `${x},${y}`;
                const cell = updated[key] || {
                    places: [],
                    objects: [],
                    robots: [],
                };

                updated[key] = {
                    ...cell,
                    places: [
                        {
                            id: name,
                            type: 'place',
                            explicit: { grid: { x, y } },
                            implicit: { relation: relation || 'none' },
                            symbolic: { label: name },
                        },
                    ], // ‚úÖ ÎçÆÏñ¥Ïì∞Í∏∞
                };
            });

            return updated;
        });

        setSelectedCells([]);
    };

    /* ===== APPLY OBJECT ===== */
    const applyObjectToSelected = () => {
        if (selectedCells.length === 0) return;

        const id = prompt('object id?');
        const type = prompt('object type?');
        const relation = prompt('relation?');
        if (!id || !type) return;

        setGridMap((prev) => {
            const updated = { ...prev };

            selectedCells.forEach(({ x, y }) => {
                const key = `${x},${y}`;
                const cell = updated[key];
                if (!cell || cell.places.length === 0) return;

                updated[key] = {
                    ...cell,
                    objects: [
                        {
                            id,
                            type,
                            explicit: { grid: { x, y } },
                            implicit: { relation: relation || 'none' },
                            symbolic: { label: type },
                        },
                    ], // ‚úÖ ÎçÆÏñ¥Ïì∞Í∏∞
                };
            });

            return updated;
        });

        setSelectedCells([]);
    };

    return (
        <div style={{ display: 'flex' }}>
            {/* MAP */}
            <div
                style={{
                    width: MAP_SIZE,
                    height: MAP_SIZE,
                    backgroundImage: "url('/map.png')",
                    backgroundSize: `${MAP_SIZE}px ${MAP_SIZE}px`,
                    backgroundRepeat: 'no-repeat',
                    position: 'relative',
                    border: '1px solid black',
                }}
                onMouseDown={(e) => {
                    draggedRef.current = false;
                    setIsDragging(true);

                    const rect = e.currentTarget.getBoundingClientRect();
                    const gx = Math.floor((e.clientX - rect.left) / GRID_SIZE);
                    const gy = Math.floor((e.clientY - rect.top) / GRID_SIZE);

                    addSelectedCell(gx, gy);
                }}
                onMouseMove={(e) => {
                    if (!isDragging) return;

                    draggedRef.current = true; // ‚≠ê ÎìúÎûòÍ∑∏ Î∞úÏÉù Í∏∞Î°ù

                    const rect = e.currentTarget.getBoundingClientRect();
                    const gx = Math.floor((e.clientX - rect.left) / GRID_SIZE);
                    const gy = Math.floor((e.clientY - rect.top) / GRID_SIZE);

                    addSelectedCell(gx, gy);
                }}
                onMouseUp={(e) => {
                    setIsDragging(false);

                    const rect = e.currentTarget.getBoundingClientRect();
                    const gx = Math.floor((e.clientX - rect.left) / GRID_SIZE);
                    const gy = Math.floor((e.clientY - rect.top) / GRID_SIZE);

                    // ‚≠ê ÎìúÎûòÍ∑∏ Ïïà ÌñàÏùÑ ÎïåÎßå toggle
                    if (!draggedRef.current) {
                        handleGridClick(gx, gy);

                        // Ï¢åÌëú ÏûÖÎ†• Î™®Îìú: ÏµúÎåÄ 5Ï†ê
                        if (coordPoints.length < 5 && !draggedRef.current) {
                            const x = Number(prompt('world x Ï¢åÌëú ÏûÖÎ†•'));
                            const y = Number(prompt('world y Ï¢åÌëú ÏûÖÎ†•'));
                            if (!isNaN(x) && !isNaN(y)) {
                                setCoordPoints(prev => [...prev, { gx, gy, x, y }]);
                            }
                        }
                    }
                }}
                onMouseLeave={() => {
                    setIsDragging(false);
                    draggedRef.current = false;
                }}
            >
                {/* GRID LINES */}
                {[...Array(GRID_COUNT)].map((_, i) => (
                    <div
                        key={`v${i}`}
                        style={{
                            position: 'absolute',
                            left: i * GRID_SIZE,
                            top: 0,
                            width: 1,
                            height: '100%',
                            background: 'rgba(0,0,0,0.2)',
                        }}
                    />
                ))}
                {[...Array(GRID_COUNT)].map((_, i) => (
                    <div
                        key={`h${i}`}
                        style={{
                            position: 'absolute',
                            top: i * GRID_SIZE,
                            left: 0,
                            height: 1,
                            width: '100%',
                            background: 'rgba(0,0,0,0.2)',
                        }}
                    />
                ))}

                {/* PLACE / OBJECT VIS */}
                {Object.entries(gridMap).map(([key, val]) => {
                    if (!val.places || val.places.length === 0) return null;

                    const [gx, gy] = key.split(',').map(Number);

                    const place = val.places[0]; // ÌòÑÏû¨ ÏÖÄÏùò ÎåÄÌëú place
                    const hasObject = val.objects && val.objects.length > 0;
                    const isPath = place.id === 'path';
                    const isWall = place.id === 'wall';

                    let bgColor = 'rgba(255,255,255,0.0)';

                    if (isPath) {
                        bgColor = 'rgba(0,255,0,0.25)'; // üü¢
                    } else if (hasObject || isWall) {
                        bgColor = 'rgba(255,0,0,0.25)'; // üî¥
                    } else if (!hasObject) {
                        bgColor = 'rgba(255,255,0,0.35)'; // üü°
                    }

                    return (
                        <div
                            key={key}
                            style={{
                                position: 'absolute',
                                left: gx * GRID_SIZE,
                                top: gy * GRID_SIZE,
                                width: GRID_SIZE,
                                height: GRID_SIZE,
                                background: bgColor,
                                pointerEvents: 'none',
                            }}
                        />
                    );
                })}

                {/* SELECTED VIS */}
                {selectedCells.map(({ x, y }) => (
                    <div
                        key={`sel-${x},${y}`}
                        style={{
                            position: 'absolute',
                            left: x * GRID_SIZE,
                            top: y * GRID_SIZE,
                            width: GRID_SIZE,
                            height: GRID_SIZE,
                            background: 'rgba(0,0,255,0.35)',
                            pointerEvents: 'none',
                        }}
                    />
                ))}
            </div>

            {/* ===== PANEL ===== */}
            <div style={{ marginLeft: 20, width: 420 }}>
                <h3>Mode</h3>
                <button onClick={() => setMode('place')}>Place</button>
                <button onClick={() => setMode('object')}>Object</button>
                <button onClick={applyGridCoordinates}>Apply Grid Coordinates</button>
                <br />
                <br />

                <button
                    onClick={
                        mode === 'place'
                            ? applyPlaceToSelected
                            : applyObjectToSelected
                    }
                >
                    Apply to Selected Cells
                </button>

                <button onClick={saveJSON} style={{ marginLeft: 10 }}>
                    Save JSON
                </button>

                <pre style={{ height: 600, overflow: 'auto' }}>
                    {JSON.stringify({ grid: gridMap }, null, 2)}
                </pre>
            </div>
        </div>
    );
}

export default App;
