import { useState } from "react";

const GRID_SIZE = 40;          // px
const RESOLUTION = 0.05;       // m / px
const ORIGIN = { x: -5, y: -5 };

function MapCanvas({ onGridClick }) {
  const handleClick = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const px = e.clientX - rect.left;
    const py = e.clientY - rect.top;

    const gx = Math.floor(px / GRID_SIZE);
    const gy = Math.floor(py / GRID_SIZE);

    onGridClick(gx, gy);
  };

  return (
    <div
      onClick={handleClick}
      style={{
        width: 800,
        height: 800,
        backgroundImage: "url('/map.png')",
        backgroundSize: "cover",
        position: "relative",
        border: "1px solid black"
      }}
    >
      {/* grid overlay */}
      {[...Array(20)].map((_, i) => (
        <div key={i} style={{
          position: "absolute",
          left: i * GRID_SIZE,
          top: 0,
          width: 1,
          height: "100%",
          background: "rgba(0,0,0,0.2)"
        }} />
      ))}
      {[...Array(20)].map((_, i) => (
        <div key={i} style={{
          position: "absolute",
          top: i * GRID_SIZE,
          left: 0,
          height: 1,
          width: "100%",
          background: "rgba(0,0,0,0.2)"
        }} />
      ))}
    </div>
  );
}

export default function App() {
  const [mode, setMode] = useState("object");
  const [semanticMap, setSemanticMap] = useState({
    objects: [],
    places: []
  });

  const gridToWorld = (gx, gy) => ({
    x: gx * GRID_SIZE * RESOLUTION + ORIGIN.x,
    y: gy * GRID_SIZE * RESOLUTION + ORIGIN.y
  });

  const handleGridClick = (gx, gy) => {
    const id = prompt("id?");
    const type = prompt("type?");

    const world = gridToWorld(gx, gy);

    if (mode === "object") {
      setSemanticMap((prev) => ({
        ...prev,
        objects: [
          ...prev.objects,
          {
            id,
            type,
            position: world
          }
        ]
      }));
    }

    if (mode === "place") {
      const size = GRID_SIZE * RESOLUTION;

      setSemanticMap((prev) => ({
        ...prev,
        places: [
          ...prev.places,
          {
            id,
            type,
            polygon: [
              [world.x, world.y],
              [world.x + size, world.y],
              [world.x + size, world.y + size],
              [world.x, world.y + size]
            ],
            center: {
              x: world.x + size / 2,
              y: world.y + size / 2
            }
          }
        ]
      }));
    }
  };

  // JSON 저장
  const saveJSON = () => {
    const blob = new Blob(
      [JSON.stringify(semanticMap, null, 2)],
      { type: "application/json" }
    );
    const url = URL.createObjectURL(blob);

    const a = document.createElement("a");
    a.href = url;
    a.download = "semantic_map.json";
    a.click();

    URL.revokeObjectURL(url);
  };

  return (
    <div style={{ display: "flex" }}>
      <MapCanvas onGridClick={handleGridClick} />

      <div style={{ marginLeft: 20, width: 400 }}>
        <button onClick={() => setMode("object")}>Object</button>
        <button onClick={() => setMode("place")}>Place</button>
        <button onClick={saveJSON}>Save JSON</button>

        <pre style={{ height: 600, overflow: "auto" }}>
          {JSON.stringify(semanticMap, null, 2)}
        </pre>
      </div>
    </div>
  );
}
