import { useEffect, useState } from "react";

/* ===== MAP PARAMS ===== */
const GRID_SIZE = 20;
const MAP_SIZE = 800;
const GRID_COUNT = MAP_SIZE / GRID_SIZE;

function App() {
  const [mode, setMode] = useState("place"); // place | object
  const [gridMap, setGridMap] = useState({});
  const [selectedCells, setSelectedCells] = useState([]);
  const [isDragging, setIsDragging] = useState(false);


  /* ===== JSON LOAD ===== */
  useEffect(() => {
    fetch("/semantic_grid_map.json")
      .then(res => (res.ok ? res.json() : null))
      .then(data => {
        if (data?.grid) setGridMap(data.grid);
      })
      .catch(() => console.log("no json"));
  }, []);

  /* ===== GRID SORT (y desc → x desc) ===== */
  const sortGridDesc = (grid) => {
    return Object.fromEntries(
      Object.entries(grid).sort(([a], [b]) => {
        const [ax, ay] = a.split(",").map(Number);
        const [bx, by] = b.split(",").map(Number);
        if (ay !== by) return by - ay;
        return bx - ax;
      })
    );
  };

  /* ===== JSON SAVE ===== */
  const saveJSON = () => {
    const sortedGrid = sortGridDesc(gridMap);

    const blob = new Blob(
      [JSON.stringify({ grid: sortedGrid }, null, 2)],
      { type: "application/json" }
    );

    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "semantic_grid_map.json";
    a.click();
    URL.revokeObjectURL(url);
  };

  /* ===== GRID CLICK (선택만 담당) ===== */
  const handleGridClick = (gx, gy) => {
    const exists = selectedCells.some(c => c.x === gx && c.y === gy);

    if (exists) {
      setSelectedCells(prev =>
        prev.filter(c => !(c.x === gx && c.y === gy))
      );
    } else {
      setSelectedCells(prev => [...prev, { x: gx, y: gy }]);
    }
  };

  /* ===== GET GRID FROM MOUSE ===== */
  const getGridFromMouse = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const gx = Math.floor((e.clientX - rect.left) / GRID_SIZE);
    const gy = Math.floor((e.clientY - rect.top) / GRID_SIZE);
    return { gx, gy };
  };


  /* ===== APPLY PLACE ===== */
  const applyPlaceToSelected = () => {
    if (selectedCells.length === 0) return;

    const name = prompt("place name?");
    const relation = prompt("relation?");
    if (!name) return;

    setGridMap(prev => {
      const updated = { ...prev };

      selectedCells.forEach(({ x, y }) => {
        const key = `${x},${y}`;
        const cell = updated[key] || { places: [], objects: [], robots: [] };

        updated[key] = {
          ...cell,
          places: [
            ...cell.places,
            {
              id: name,
              type: "place",
              explicit: { grid: { x, y } },
              implicit: { relation: relation || "none" },
              symbolic: { label: name }
            }
          ]
        };
      });

      return updated;
    });

    setSelectedCells([]);
  };

  /* ===== APPLY OBJECT ===== */
  const applyObjectToSelected = () => {
    if (selectedCells.length === 0) return;

    const id = prompt("object id?");
    const type = prompt("object type?");
    const relation = prompt("relation?");
    if (!id || !type) return;

    setGridMap(prev => {
      const updated = { ...prev };

      selectedCells.forEach(({ x, y }) => {
        const key = `${x},${y}`;
        const cell = updated[key];
        if (!cell || cell.places.length === 0) return;

        updated[key] = {
          ...cell,
          objects: [
            ...cell.objects,
            {
              id,
              type,
              explicit: { grid: { x, y } },
              implicit: { relation: relation || "none" },
              symbolic: { label: type }
            }
          ]
        };
      });

      return updated;
    });

    setSelectedCells([]);
  };

  return (
    <div style={{ display: "flex" }}>
      {/* ===== MAP ===== */}
      <div
        style={{
          width: MAP_SIZE,
          height: MAP_SIZE,
          backgroundImage: "url('/map.png')",
          backgroundSize: `${MAP_SIZE}px ${MAP_SIZE}px`,
          position: "relative",
          border: "1px solid black"
        }}
        onClick={e => {
          const rect = e.currentTarget.getBoundingClientRect();
          const gx = Math.floor((e.clientX - rect.left) / GRID_SIZE);
          const gy = Math.floor((e.clientY - rect.top) / GRID_SIZE);
          handleGridClick(gx, gy);
        }}
      >
        {/* GRID LINES */}
        {[...Array(GRID_COUNT)].map((_, i) => (
          <div
            key={`v${i}`}
            style={{
              position: "absolute",
              left: i * GRID_SIZE,
              top: 0,
              width: 1,
              height: "100%",
              background: "rgba(0,0,0,0.2)"
            }}
          />
        ))}
        {[...Array(GRID_COUNT)].map((_, i) => (
          <div
            key={`h${i}`}
            style={{
              position: "absolute",
              top: i * GRID_SIZE,
              left: 0,
              height: 1,
              width: "100%",
              background: "rgba(0,0,0,0.2)"
            }}
          />
        ))}

        {/* PLACE / OBJECT VIS */}
        {Object.entries(gridMap).map(([key, val]) => {
          if (!val.places.length) return null;
          let bgColor = "rgba(255,255,255,0.0)";

          const [gx, gy] = key.split(",").map(Number);
          const hasObject = val.objects.length > 0;

          if(hasObject) bgColor = "rgba(0,255,0,0.25)"
          else if(!hasObject) bgColor = "rgba(255,255,0,0.35)"

          if(val.places[0].symbolic?.category === "path") bgColor = "rgba(255,0,0,0.25)"

          return (
            <div
              key={key}
              style={{
                position: "absolute",
                left: gx * GRID_SIZE,
                top: gy * GRID_SIZE,
                width: GRID_SIZE,
                height: GRID_SIZE,
                background: bgColor,
                pointerEvents: "none"
              }}
            />
          );
        })}


        {/* SELECTED VIS */}
        {selectedCells.map(({ x, y }) => (
          <div
            key={`sel-${x},${y}`}
            style={{
              position: "absolute",
              left: x * GRID_SIZE,
              top: y * GRID_SIZE,
              width: GRID_SIZE,
              height: GRID_SIZE,
              background: "rgba(0,0,255,0.35)",
              pointerEvents: "none"
            }}
          />
        ))}
      </div>

      {/* ===== PANEL ===== */}
      <div style={{ marginLeft: 20, width: 420 }}>
        <h3>Mode</h3>
        <button onClick={() => setMode("place")}>Place</button>
        <button onClick={() => setMode("object")}>Object</button>

        <br /><br />

        <button
          onClick={mode === "place"
            ? applyPlaceToSelected
            : applyObjectToSelected}
        >
          Apply to Selected Cells
        </button>

        <button onClick={saveJSON} style={{ marginLeft: 10 }}>
          Save JSON
        </button>

        <pre style={{ height: 600, overflow: "auto" }}>
          {JSON.stringify({ grid: gridMap }, null, 2)}
        </pre>
      </div>
    </div>
  );
}

export default App;
