import { useEffect, useState, useRef } from 'react';

/* ===== MAP PARAMS ===== */
const GRID_SIZE = 20;
const GRID_COLS = 145; // 가로
const GRID_ROWS = 102; // 세로

const MAP_WIDTH = GRID_COLS * GRID_SIZE;
const MAP_HEIGHT = GRID_ROWS * GRID_SIZE;

const MAP_SIZE = 800;
const GRID_COUNT = MAP_SIZE / GRID_SIZE;

function App() {
  const [mode, setMode] = useState('place'); // place | object
  const [gridMap, setGridMap] = useState({});
  const [selectedCells, setSelectedCells] = useState([]);
  const [isDragging, setIsDragging] = useState(false);
  const draggedRef = useRef(false);

  /* ===== JSON LOAD ===== */
  useEffect(() => {
    fetch('/semantic_grid_map.json')
      .then(res => (res.ok ? res.json() : null))
      .then(data => {
        if (data?.grid) setGridMap(data.grid);
      })
      .catch(() => console.log('no json'));
  }, []);

  /* ===== GRID SORT ===== */
  const sortGridDesc = (grid) => {
    return Object.fromEntries(
      Object.entries(grid).sort(([a], [b]) => {
        const [ax, ay] = a.split(',').map(Number);
        const [bx, by] = b.split(',').map(Number);
        if (ay !== by) return by - ay;
        return bx - ax;
      })
    );
  };

  /* ===== JSON SAVE ===== */
  const saveJSON = () => {
    const sortedGrid = sortGridDesc(gridMap);
    const blob = new Blob(
      [JSON.stringify({ grid: sortedGrid }, null, 2)],
      { type: 'application/json' }
    );
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'semantic_grid_map.json';
    a.click();
    URL.revokeObjectURL(url);
  };

  /* ===== GRID CLICK ===== */
  const handleGridClick = (gx, gy) => {
    setSelectedCells(prev => {
      const exists = prev.some(c => c.x === gx && c.y === gy);
      return exists
        ? prev.filter(c => !(c.x === gx && c.y === gy))
        : [...prev, { x: gx, y: gy }];
    });
  };

  const addSelectedCell = (gx, gy) => {
    setSelectedCells(prev => {
      if (prev.some(c => c.x === gx && c.y === gy)) return prev;
      return [...prev, { x: gx, y: gy }];
    });
  };

  /* ===== APPLY PLACE ===== */
  const applyPlaceToSelected = () => {
    if (selectedCells.length === 0) return;

    const name = prompt('place name?');
    const relation = prompt('relation?');
    if (!name) return;

    setGridMap(prev => {
      const updated = { ...prev };

      selectedCells.forEach(({ x, y }) => {
        const key = `${x},${y}`;
        const cell = updated[key] || {
          places: [],
          objects: [],
          robots: [],
        };

        updated[key] = {
          ...cell,
          places: [
            {
              id: name,
              type: 'place',
              explicit: { grid: { x, y } },
              implicit: { relation: relation || 'none' },
              symbolic: { label: name },
            },
          ],
        };
      });

      return updated;
    });

    setSelectedCells([]);
  };

  /* ===== APPLY OBJECT ===== */
  const applyObjectToSelected = () => {
    if (selectedCells.length === 0) return;

    const id = prompt('object id?');
    const type = prompt('object type?');
    const relation = prompt('relation?');
    if (!id || !type) return;

    setGridMap(prev => {
      const updated = { ...prev };

      selectedCells.forEach(({ x, y }) => {
        const key = `${x},${y}`;
        const cell = updated[key];
        if (!cell || cell.places.length === 0) return;

        updated[key] = {
          ...cell,
          objects: [
            {
              id,
              type,
              explicit: { grid: { x, y } },
              implicit: { relation: relation || 'none' },
              symbolic: { label: type },
            },
          ],
        };
      });

      return updated;
    });

    setSelectedCells([]);
  };

  return (
    <div style={{ display: 'flex' }}>
        {/* ===== MAP WRAPPER ===== */}
    <div
      style={{
        width: '100vw',
        height: '100vh',
        overflow: 'auto',
        background: '#222',
      }}
    >
      <div
        style={{
          transform: 'scale(0.6)',     // ⭐ 여기서 비율 조절
          transformOrigin: 'top left',
        }}
      ></div></div>
      {/* MAP */}
      <div
        style={{
    width: MAP_WIDTH,
    height: MAP_HEIGHT,
    backgroundImage: "url('/map.png')",
    backgroundSize: `${MAP_WIDTH}px ${MAP_HEIGHT}px`,
    backgroundRepeat: 'no-repeat',
    position: 'relative',
    border: '1px solid black',
  }}
        onMouseDown={(e) => {
          draggedRef.current = false;
          setIsDragging(true);

          const rect = e.currentTarget.getBoundingClientRect();
          const gx = Math.floor((e.clientX - rect.left) / GRID_SIZE);
          const gy = Math.floor((e.clientY - rect.top) / GRID_SIZE);

          addSelectedCell(gx, gy);
        }}
        onMouseMove={(e) => {
          if (!isDragging) return;

          draggedRef.current = true;

          const rect = e.currentTarget.getBoundingClientRect();
          const gx = Math.floor((e.clientX - rect.left) / GRID_SIZE);
          const gy = Math.floor((e.clientY - rect.top) / GRID_SIZE);

          addSelectedCell(gx, gy);
        }}
        onMouseUp={(e) => {
          setIsDragging(false);

          const rect = e.currentTarget.getBoundingClientRect();
          const gx = Math.floor((e.clientX - rect.left) / GRID_SIZE);
          const gy = Math.floor((e.clientY - rect.top) / GRID_SIZE);

          if (!draggedRef.current) {
            handleGridClick(gx, gy);
          }
        }}
        onMouseLeave={() => {
          setIsDragging(false);
          draggedRef.current = false;
        }}
      >
        {/* GRID LINES */}
        {[...Array(GRID_COLS + 1)].map((_, i) => (
  <div
    key={`v${i}`}
    style={{
      position: 'absolute',
      left: i * GRID_SIZE,
      top: 0,
      width: 1,
      height: '100%',
      background: 'rgba(0,0,0,0.2)',
    }}
  />
))}

        {[...Array(GRID_ROWS + 1)].map((_, i) => (
  <div
    key={`h${i}`}
    style={{
      position: 'absolute',
      top: i * GRID_SIZE,
      left: 0,
      height: 1,
      width: '100%',
      background: 'rgba(0,0,0,0.2)',
    }}
  />
))}



        {/* PLACE / OBJECT VIS */}
        {Object.entries(gridMap).map(([key, val]) => {
          if (!val.places || val.places.length === 0) return null;

          const [gx, gy] = key.split(',').map(Number);
          const place = val.places[0];
          const hasObject = val.objects && val.objects.length > 0;

          let bgColor = 'rgba(255,255,255,0.0)';
          if (place.id === 'path') bgColor = 'rgba(0,255,0,0.25)';
          else if (hasObject || place.id === 'wall') bgColor = 'rgba(255,0,0,0.25)';
          else bgColor = 'rgba(255,255,0,0.35)';

          return (
            <div
              key={key}
              style={{
                position: 'absolute',
                left: gx * GRID_SIZE,
                top: gy * GRID_SIZE,
                width: GRID_SIZE,
                height: GRID_SIZE,
                background: bgColor,
                pointerEvents: 'none',
              }}
            />
          );
        })}

        {/* SELECTED VIS */}
        {selectedCells.map(({ x, y }) => (
          <div
            key={`sel-${x},${y}`}
            style={{
              position: 'absolute',
              left: x * GRID_SIZE,
              top: y * GRID_SIZE,
              width: GRID_SIZE,
              height: GRID_SIZE,
              background: 'rgba(0,0,255,0.35)',
              pointerEvents: 'none',
            }}
          />
        ))}
      </div>

      {/* PANEL */}
      <div style={{ marginLeft: 20, width: 420 }}>
        <h3>Mode</h3>
        <button onClick={() => setMode('place')}>Place</button>
        <button onClick={() => setMode('object')}>Object</button>

        <br /><br />

        <button onClick={mode === 'place' ? applyPlaceToSelected : applyObjectToSelected}>
          Apply to Selected Cells
        </button>

        <button onClick={saveJSON} style={{ marginLeft: 10 }}>
          Save JSON
        </button>

        <pre style={{ height: 600, overflow: 'auto' }}>
          {JSON.stringify({ grid: gridMap }, null, 2)}
        </pre>
      </div>
    </div>
  );
}

export default App;
